import React, { Component } from 'react';
import  { connect } from 'react-redux';
import './Users.scss';

import { deleteUserAction, dispatchUsersAction } from '../Actions/Actions';
import { NavLink } from 'react-router-dom';


function searchingFor(term){
  return function(x){
    return x.nom_prenom.toLowerCase().includes(term.toLowerCase()) || ! term;
  }
}

class Users extends Component {
    constructor() {
        super();
        this.state = {
          term:'',
        }
        this.searchHandler = this.searchHandler.bind(this);
    }

    searchHandler(event){
      this.setState({term:event.target.value}
      )}

    onClickDelete = (userId) => {
      this.props.deleteUser(userId)
    }

    render() {
      const{term} = this.state;
      return (
        <div className="c-users">
        <form className="c-users__form">
          <input className="c-users__text" 
            onChange={this.searchHandler}
            value={term}
          />
        </form>

        <NavLink to="/AddUser">
          <img className="c-users__add" src={process.env.PUBLIC_URL+'/assets/images/add-user.svg'} width="30px" alt="icon" />

        </NavLink>
 !
          <div className="c-card"> 
                  {this.props.users.filter(searchingFor(term)).map((item, index) => {
                      return (
                        <div key={index}>
                          <p className="ligne"><span className="c-card__item">{item.nom_prenom} </span> !== "" ?
                            {/* <span className="c-card__envelope"><img src={process.env.PUBLIC_URL + '/assets/images/envelope.svg'} width="15px" alt="Icones"/></span> */}
                            <span className="c-card__waste"><img src={process.env.PUBLIC_URL + '/assets/images/waste.svg' } onClick={() => this.onClickDelete(item.id) } width="15px" alt="Icones"/></span>
                            </p> 
                            :
                            <img className="c-header__logo" src={process.env.PUBLIC_URL+'/assets/images/logo_fusee.png'} alt="Logo Smile" /> */
                         </div>
                      )
                    })}
          </div>
        </div>
      );
    }
  }

  const mapStateToProps = store => {
    return {
      users: store.users
    }
  }

   const mapDispatchToProps =  {
    deleteUser: deleteUserAction, 
    dispatchUsers:  dispatchUsersAction,
  }

  export default connect ( mapStateToProps, mapDispatchToProps)(Users);
