import axios from 'axios'

export const SEND = 'app: dispatchUsersAction';
export const ADD_USER = 'users: addUsersAction';
export const DELETE_USER = 'users: deleteUserAction';

const apiUrl = 'http://localhost:6999/users';

export const dispatchUsersAction = (users) => {
  console.log(users)
    return {
        type: SEND,
        payload: {
            users: users
        }
    }
}

// export const dispatchUsersAction = (users) => {
//   return {
//     type: SEND_USERS, 
    // payload: {
//     users
//   }
// };

export const addUsersAction = ({ nom_prenom, email }) => {
  return (dispatch) => {
    return axios.post(`${apiUrl}/insert`, {nom_prenom, email})
      .then(response => {
        dispatch(addUserSuccess(response.data))
      })
      .catch(error => {
        throw(error);
      });
  };
};

export const addUserSuccess =  (data) => {
  return {
    type: ADD_USER,
    payload: {
      _id: data._id,
      nom_prenom: data.nom_prenom,
      email: data.email
    }
  }
};

export const deleteUserSuccess = (data) => {
  return {
    type: DELETE_USER,
    payload: {
      _id: data._id,
      nom_prenom: data.nom_prenom,
      email: data.email
    }
  }
}

export const deleteUserAction = ({ nom_prenom, email }) => {
  return (dispatch) => {
    return axios.delete(`${apiUrl}/delete/${nom_prenom, email}`)
      .then(response => {
        dispatch(deleteUserSuccess(response.data))
      })
      .catch(error => {
        throw(error);
      });
  };
};







