import axios from 'axios';
import { toastr } from 'react-redux-toastr';
import underscore from 'underscore';

// const apiUrl = 'http://localhost:6999/users';
const apiSendUsersUrl = 'http://localhost:6999/send';

const toastrOptions = {
  timeOut: 3000,
  onCloseButtonClick: () => console.log('Close button was clicked'),
  showCloseButton: false,
};

export const ADD_WINNERS_BY_WEEK = 'winners: addWinnersByWeekAction';
export const MAIL_WINNERS = 'winners: mailWinnersAction';
export const WINNERS_VALIDATED = 'WINNERS_VALIDATED action';

const winnersSelected = users => {
  return {
    type: MAIL_WINNERS,
    payload: {
      users,
    },
  };
};

const winnersValidated = () => {
  return {
    type: WINNERS_VALIDATED,
  };
};

export const selectWinnersAction = () => {
  return (dispatch, getState) => {
    const users = getState().users;
    const sampled = underscore.sample(users, 3);
    dispatch(winnersSelected(sampled));
    this.setState({logoAnimated : !this.state.logoAnimated ? 'logoSpeed' : 'logo'})

  };
};

export const validateWinnersAction = () => {
  return (dispatch, getState) => {
    const state = getState();
    const winners =
      state.winnersByWeek.length > 0
        ? state.winnersByWeek[state.winnersByWeek.length - 1].winners
        : [];
    const sampled = underscore.sample(winners, 3);
    const emails = sampled.map(item => item.email);
    return axios
      .post(apiSendUsersUrl, { emails })
      .then(response => {
        toastr.success(
          'Confirmation',
          'Un mail a bien été envoyé aux 3 participants',
          toastrOptions,
        );
        dispatch(winnersValidated());
      })
      .catch(error => {
        throw error;
      });
  };
};
