export default function Reducers(state = [], action) {
  switch (action.type) {
    case ADD_USER:
      return [...state, action.payload];
    case DELETE_USER:
      return state.filter(users => users._nom_prenom !== action.payload._nom_prenom);
      case SEND:
      return [ ...state, ...payload.users]
    default:
      return state;
  }
}
