import React, { Component } from 'react';
import './Users.scss';

import axios from 'axios';
import underscore from 'underscore';

class Users extends Component {
    constructor(props) {
        super(props);
    }

     //New syntax async// await
  async componentDidMount() {
    //upon loading, I store in the state the result of my fetch
    const result = await axios.get('http://localhost:6999/users')
    this.setState({users: result.data}, () => {
        this.sampleList();
    });
  }

    render() {
      return (
        <div className="c-users">            
              <div>
                <div class="card">
                  <li>{this.props.selectedItems}</li>                  
                </div>  
              </div>  
         </div>
      );
    }
  }


  export default Users;
