import { SEND, DELETE_USER, ADD_USER, COPY_USER } from '../Actions/userActions';

const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD_USER:
      return [...state, action.payload];
    case DELETE_USER:
      return state.filter(users => users.id !== action.payload.id);
    case SEND:
      return [...state, ...action.payload.users];
    case COPY_USER:
      return [...state, payload.users];
    default:
      return state;
  }
};
