import React, { Component } from 'react';
import './Users.scss';

function searchingFor(term){
  return function(x){
    return x.nom_prenom.toLowerCase().includes(term.toLowerCase()) || ! term;
  }
}

class Users extends Component {
    constructor(props) {
        super(props);
        this.state = {
          term:'',
        }

        this.searchHandler = this.searchHandler.bind(this);
    }

    searchHandler(event){
      this.setState({term:event.target.value})
    }

    render() {
      const{term} = this.state;
      return (
        <div className="c-users">
        <form>
          <input type="c-users__input" 
            onChange={this.searchHandler}
            value={term}
          />
        </form>

          <div className="c-card"> 
                  {this.props.users.filter(searchingFor(term)).map((item, index) => {
                      return (
                        <div key={index}>
                          <p><span className="c-card__item">{item.nom_prenom}</span>
                          <span className="c-card__icon"><img src={process.env.PUBLIC_URL + '/assets/images/envelope.svg'} width="15px" alt="Icones"/></span>
                            <span className="c-card__icon"><img src={process.env.PUBLIC_URL + '/assets/images/waste.svg' } width="15px" alt="Icones"/></span>
                            </p> 
                         </div>
                      )
                    })}
          </div>
        </div>
      );
    }
  }


  export default Users;
