import { SEND, ADD, REMOVE } from '../Actions/Actions';

const Reducers = (state = [], {type, payload}) => {
    switch (type) {
      case SEND:
        return [ ...state, payload.users]
        default:
        return state;
      case ADD:
        return [ ...state, payload.id]
      case REMOVE:
        return [ ...state.filter(function(item) {
          return item !==payload.id})]
    //  case RANDOM:
    //  return  [ ...state.sample(function(item)]
        // return item !==payload.id})]
    }  
}

export default Reducers;
