import { WINNERS_VALIDATED, MAIL_WINNERS } from '../Actions/winnerActions';
import { RESET_WINNERS } from '.../Actions/winnerActions';


const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case MAIL_WINNERS:
      return [
        ...state,
        {
          date: new Date(),
          winners: action.payload.users,
          alreadyMailed: false,
        },
      ];
    case WINNERS_VALIDATED:
      return [
        ...state.slice(0, -1),
        {
          ...state[state.length - 1],        },
      ];
    case RESET_WINNERS:
      return [...state[state.length < 3], ...state];

    default:
      return state;
    }
  };
