import React, { Component } from 'react';
import './Winners.scss';

import axios from 'axios';
import underscore from 'underscore';
import { connect } from 'react-redux';

class Winners extends Component {
  constructor() {
      super();
      this.state = {
          users: [],
          selectedItems: [],
          alreadySelected: []
      }
  }

  //New syntax async// await
  async componentDidMount() {
    //upon loading, I store in the state the result of my fetch
    const result = await axios.get('http://localhost:6999/users')
    this.setState({users: result.data}, () => {
        this.sampleList();
    });

    // this.props.setUsers(result.data, () => {
    //     this.sampleList();
    // });
  }

  //.sample is a method to produce a random list of 3 people
  //the sampled result is stored in the new table selectedItems
  sampleList = () => {
      const sampled = underscore.sample(this.state.users,3)
    //   method 
      this.setState({
        selectedItems: sampled,
      }, this.addSelected)
  }
  //create a method that adds 3 persons in array alreadySelected// apply onClick
  addSelected = () => {
    this.setState({
        alreadySelected: [...this.state.alreadySelected, ...this.state.selectedItems]
    })
  }

    render() {
        return (
            <div className="c-winners">
             <h2 className="c-winners__subtitle">Et les gagnants sont ...</h2> 
                  {this.state.selectedItems.map((item, index) => {
                      return (
                        <div>  
                             <input className="c-winners__input"
                              type="c-winners__text"
                              key={index}
                              Value={item.nom_prenom}
                               />
                        </div>  
                      )
                    })}
            </div>
        )
    }
}

const mapDispatchToProps = {

  }
  const mapStateToProps = state=> ({
    users: store.users
 })
  export default connect ( mapStateToProps, mapDispatchToProps)(Winners);
