import React, { Component } from 'react';
import './App.scss';

import Winners from './Components/Winners';
import Users from './Components/Users';
import SearchBar from './Components/SearchBar';

import { BrowserRouter, Route, Switch, Link } from 'react-router-dom';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        users: []
    }
  }

  setUsers = (users, callback) => {
    this.setState({users}, callback);
  }

  render() {
    return (
      <main className="c-app">
         <BrowserRouter>
          <Switch>
            {/* <Route path="/users" component={Users} exact/> */}
          </Switch>
          </BrowserRouter>
          <div className="c-header">
            <img className="c-header__logo" src={process.env.PUBLIC_URL+'/assets/images/logo_fusee.png'} alt="Logo Smile" />
          </div>
          <div className="c-content">
            <h1 className="c-content__title">BREAKFAST TIME!</h1>
              <h2 className="c-content__subtitle">Et les gagnants sont ...</h2> 
              <Winners
                setUsers={this.setUsers}
                users={this.state.users}
              />
              {/* <SearchBar /> */}
              <Users users={this.state.users}/>
          </div>
        </main>
    );
  }
}


export default App;


