import  React, { Component } from 'react';
import './AddUser.scss';

import  { connect } from 'react-redux';

import { addUsersAction } from '../Actions/Actions'


class Adduser extends Component {
  constructor(){
    super();
    this.state = {
      nom_prenom:"",
      email:"",
    }
    // this.disableSubmitButton = true;
  }

  handleChange = (event) => {
    this.setState({[event.target.id]: event.target.value});
  }

  handleSubmit = (event) => {
    this.props.addUsersAction(event)
  }

  render() {
      // if(this.state.email.length > 0 &&
      //   this.state.nom_prenom.length > 0) {
      //     this.disableSubmitButton = false;
      //   } else {
      //   this.disableSubmitButton = true;
      //   }
        return (

          <div className="c-addUser">
          <h2 className="c-addUser__subtitle">Inscrivez des nouveaux participants ...</h2>
            <div className="c-card"> 
              <form onSubmit = {this.props.handleSubmit}> 
                <fieldset className="c-card__fieldset">
                  <input className="c-card__form-text" id="nom_prenom" type="text" placeholder="Nom PrÃ©nom" onChange={this.handleChange}/>
                </fieldset>
                <fieldset className="c-card__fieldset">
                <input className="c-card__email" type="email" placeholder="prenom.nom@smile.fr" onChange={this.handleChange}/>
                </fieldset>  
                <fieldset className="c-card__fieldset">
                  <button  className="c-card__submit" value="Envoyer" >Envoyer</button>
                </fieldset>
              </form>
            </div>
          </div>
        )
    }
}

const mapStateToProps = store=> ({
  users: store.users
});

const mapDispatchToProps = {
 addUsers: addUsersAction
}

export default connect (mapStateToProps, mapDispatchToProps)(Adduser);
