              </fieldset>
              <fieldset>
               <input id="c-card__email" type="email" placeholder="prenom.nom@smile.fr" onChange={this.handleChange}/>
              </fieldset>  
              <fieldset>
                <button  type="c-card__submit" value="Envoyer" >Envoyer</button>
              </fieldset>
            </form>
          </div>
        )
    }
}

