import React, { Component } from 'react';
import './App.scss';

import Winners from './Components/Winners';
import Users from './Components/Users';
import AddUser from './Components/AddUser'


import axios from 'axios';
import { BrowserRouter, Switch, NavLink, Route } from 'react-router-dom';
// import Calendar from 'react-calendar';
import { connect } from 'react-redux';
import { dispatchUsersAction  } from './Actions/userActions';
import { selectWinnersAction } from '././Actions/winnerActions';


class App extends Component {

  onSelectWinner = () => {
    this.props.selectNewWinners();
  }

  async componentDidMount() {
    const result = await axios.get('http://localhost:6999/users');
    this.props.dispatchUsers(result.data);
  }

  // handleChangeDonut = () => {
  //   this.setState({logoAnimated : !this.state.logoAnimated ? 'logoSpeed' : 'logo'})
  // }


  render() {
    const logoAnimated = 'logoSpeed';

    return (
      <BrowserRouter>
      <main className="c-app">
        <div className="c-header">
          <NavLink to="/">
            <img className={this.logoAnimated ? "c-header__logo" : "c-header__logoSpeed"} src={process.env.PUBLIC_URL+'/assets/images/donut.svg'} width="8%" alt="Logo donut" />
          </NavLink>
          <NavLink to="/Users">
            <img className="c-header__user" src={process.env.PUBLIC_URL+'/assets/images/users.svg'} width="30px" alt="icon users" />
          </NavLink>
          {/* <NavLink to="/Calendar">
             <img className="c-header__calendar" src={process.env.PUBLIC_URL+'/assets/images/daily-calendar.svg'} width="30px" alt="icon" />
          </NavLink> */}
          </div>
        <div className="c-content">
          <h1 className="c-content__title">BREAKFAST TIME</h1>

            <Switch>
              <Route exact path="/" component={Winners} />
              <Route path="/Users" component={Users} />
              {/* <Route path="/Calendar" component={Calendar}/> */}
              <Route path ="/AddUser" component={AddUser} />
            </Switch>
         </div>
      </main>
      </BrowserRouter>  
    );
  }
}

const mapStateToProps = store => {
  return {
    users: store.users
  }
}
const mapDispatchToProps = {
  dispatchUsers:  dispatchUsersAction,
  selectNewWinners: selectWinnersAction,
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
