import React, { Component } from 'react';
import './App.scss';

import Winners from './Components/Winners';
import Users from './Components/Users';

import axios from 'axios';
import { BrowserRouter, Switch, NavLink, Route } from 'react-router-dom';
// import Calendar from 'react-calendar';
import { connect } from 'react-redux';
import { dispatchUsersAction } from '../Actions/UsersActions';



class App extends Component {
  constructor() {
    super();
    this.state = {
      users: []
    }
  }

  async componentDidMount() {
  const result = await axios.get('http://localhost:6999/users')
  .then ((result))({users: result.data}, () => {
      this.props.dispatchUsers(this.state.users)
  });
}

  render() {
    return (
      <BrowserRouter>
      <main className="c-app">
        <div className="c-header">
            <img className="c-header__logo" src={process.env.PUBLIC_URL+'/assets/images/logo_fusee.png'} alt="Logo Smile" />

             <NavLink to="/Users">
               <img className="c-header__user" src={process.env.PUBLIC_URL+'/assets/images/users.svg'} width="30px" alt="icon users" />
            </NavLink>
            <NavLink to="/Calendar">
             <img className="c-header__calendar" src={process.env.PUBLIC_URL+'/assets/images/daily-calendar.svg'} width="30px" alt="icon" />
            </NavLink>
          </div>
        <div className="c-content">
          <h1 className="c-content__title">BREAKFAST TIME!</h1>

            <Switch>
              <Route exact path="/" component={Winners} />
              <Route path="/Users" component={Users} />
              {/* <Route path="/Calendar" component={Calendar}/> */}
            </Switch>
         </div>
      </main>
      </BrowserRouter>  
    );
  }
}


const mapActionToProps = {
  dispatchUsers:  dispatchUsersActions,
}
export default connect (null, mapActionToProps)(App);
