import  React, { Component } from 'react';
import './AddUser.scss';

import axios from 'axios';


class Adduser extends Component {
  constructor(){
    super();
    this.state = {
      nom_prenom:"",
      email:"",
    }
    this.disableSubmitButton = true;
  }

  handleChange = (event) => {
    this.setState({[event.target.id]: event.target.value});
  }

  handleSubmit = () => {
     axios.post("http://localhost:6999/insert", {
      nom_prenom:this.state.nom_prenom,
      email: this.state.email,
   })
    .then((result) => {
      console.log(result.data)
    })
    .catch((err) => {
      console.log(err);
    })
  }


    render() {
      if(this.state.email.length > 0 &&
        this.state.nom_prenom.length > 0) {
          this.disableSubmitButton = false;
        } else {
        this.disableSubmitButton = true;
        }
        return (
          <div className="c-card">
            <form > 
              <fieldset>
                <input class="form-text required" id="nom_prenom" type="text" placeholder="Nom PrÃ©nom" onChange={this.handleChange}/>
              </fieldset>
              <fieldset>
               <input id="email" type="email" placeholder="prenom.nom@smile.fr" onChange={this.handleChange}/>
              </fieldset>  
              <fieldset>
                <button  type="submit" value="Envoyer" disabled={this.disableSubmitButton} onClick={this.handleSubmit}>Envoyer</button>
              </fieldset>
            </form>
          </div>
        )
    }
}

export default Adduser;
