import { SEND, DELETE_USER, ADD_USER } from '../Actions/Actions';


const initialState =[]

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD_USER:
      return [...state,action.payload];
    case DELETE_USER:
      return state.filter(users => users._id !== action.user.id);
      // case DELETE_USER:
    //   return state.filter(users => users._nom_prenom !== action.payload._nom_prenom);
    case SEND:
        return [ ...state, ...action.payload.users]
    default:
      return state;
  }
}

