import React, { Component } from 'react';
import './App.scss';

import Winners from './Components/Winners';
import Users from './Components/Users';

import { BrowserRouter, Switch, NavLink, Route } from 'react-router-dom';
import Calendar from 'react-calendar';
// import { spring, AnimatedSwitch } from 'react-router-transition'
// import topFunction from './Utils/Utils'


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        users: []
    }
    this.users = this.users.bind(this)
  }

  setUsers = (users, callback) => {
    this.setState({users}, callback);
  }

  render() {
    return (
      <main className="c-app">
         <BrowserRouter>
          <Switch>
            <Route path="/users" component={Users} exact/>
          </Switch>
          </BrowserRouter>
          <div className="c-header">
            <img className="c-header__logo" src={process.env.PUBLIC_URL+'/assets/images/logo_fusee.png'} alt="Logo Smile" />
             <NavLink to="Users" onClick = {users} >
           <img className="c-footer__icon" src={process.env.PUBLIC_URL+'/assets/images/users.svg'} width="30px" alt="icon users" />
            </NavLink>

            <NavLink to="Users" onClick={() => topFunction()}>
            <img className="c-footer__icon" src={process.env.PUBLIC_URL+'/assets/images/daily-calendar.svg'} width="30px" alt="icon" calendar/>
            </NavLink>
          </div>
          <div className="c-content">
            <h1 className="c-content__title">BREAKFAST TIME!</h1>
              <Winners
                setUsers={this.setUsers}
                users={this.state.users}
              />
              <Users users={this.state.users}/>
          </div>
        </main>
    );
  }
}

export default App;
