import React, { Component } from 'react'
import './Winners.css'
import axios from 'axios';

import underscore from 'underscore';

class Winners extends Component {
  constructor() {
      super();
      this.state = {
          liste: [],
          selectedItems: []
      }
  }

  async componentDidMount() {
    //dès le chargement , je stocke dans le state, le résultat de mon fetch
    const result = await axios.get('http://localhost:6999/liste')

    this.setState({liste: result.data}, () => {
        this.sampleList();
    });
  }

  //Méthode permettant de produire une liste aléatoire de 3 personnes
  //le setState ne lit pas les index d'un tableau, donc il faut déclarer une variable, et lui donner la valeur de "liste".
  sampleList = () => {
      const sampled = underscore.sample(this.state.liste, 3)
      //sampled[index, nom_prenom] = ! this.state.liste[index, nom_prenom]
      this.setState({
        selectedItems: sampled,
      })
  }

    render() {
        return (
            <div>
                 {this.state.selectedItems.map((item, index) => {
                    return (
                        <div>
                            <p key={index}>
                            <p>{item.nom_prenom}</p>
                            </p> 
                        </div> 
                    )
                })}
            </div>
        )
    }
}

export default Winners;
