  // }

  // //create a method that adds 3 persons in array alreadySelected// apply onClick
  // addSelected = () => {
  //   this.setState({
  //       alreadySelected: [...this.state.alreadySelected, ...this.state.selectedItems]
  //   })
  // }

  onSelectWinner = () => {
    this.props.selectNewWinners();
  }

  onValidateWinner = () => {
    this.props.validateWinners();
  }


    render() {
      //ici je fais appelle à this.props.users car c'est le state général

        return (
            <div className="c-winners">
             <h2 className="c-winners__subtitle">Et les gagnants sont ...</h2> 
             {/* <img className="c-winners__logo" src={process.env.PUBLIC_URL+'/assets/images/coffee-cup.svg'} width="50px" alt="Logo coffee-cup" /> */}
                  {this.props.winners.map(winner => (
                    <div key={winner.id}>  
                        <div className="c-winners__input">
                          {winner.nom_prenom}
                        </div>
                    </div>  
                  )) }
              <button className="c-winners__button" onClick={this.onSelectWinner}>Sélectionne des gagnants!</button>
             {this.props.winners.length > 0 &&
                <button className="c-winners__button" onClick={this.onValidateWinner}>Préviens les gagnants!</button>
              }
            </div>
        )
    }
}

const mapStateToProps = state => ({
    winners: state.winnersByWeek.length > 0 ? state.winnersByWeek[state.winnersByWeek.length - 1].winners : []
})


const mapDispatchToProps = {
  selectNewWinners: selectWinnersAction,
  validateWinners: validateWinnersAction

}

export default connect(mapStateToProps, mapDispatchToProps )(Winners);
