import React, { Component } from 'react';

import './Users.scss';

import axios from 'axios';
import { connect } from 'react-redux';
import { removeUsersAction, dispatchUsersAction } from '../Actions/Actions';
import { NavLink } from 'react-router-dom';


function searchingFor(term){
  return function(x){
    return x.nom_prenom.toLowerCase().includes(term.toLowerCase()) || ! term;
  }
}

class Users extends Component {
    constructor() {
        super();
        this.state = {
          term:'',
        }
        this.searchHandler = this.searchHandler.bind(this);
    }

    searchHandler(event){
      this.setState({term:event.target.value})
    }

    onClickDelete =() => {
      let nom_prenom = this.state.deleteNom_prenom
      axios.delete(`http://localhost:6999/delete/${nom_prenom}`)
      .then((result) => {
        console.log(result.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

    render() {
      const{term} = this.state;
      return (
        <div className="c-users">
        <form className="c-users__form">
          <input type="c-users__text" 
            onChange={this.searchHandler}
            value={term}
          />
        </form>
        <NavLink to="/AddUser">
          <img className="c-users__add" src={process.env.PUBLIC_URL+'/assets/images/add-user.svg'} width="30px" alt="icon" />
        </NavLink>

          <div className="c-card"> 
                  {this.props.users.filter(searchingFor(term)).map((item, index) => {
                      return (
                        <div key={index}>
                          <p className="ligne"><span className="c-card__item">{item.nom_prenom}</span>
                            <span className="c-card__envelope"><img src={process.env.PUBLIC_URL + '/assets/images/envelope.svg'} width="15px" alt="Icones"/></span>
                            <span className="c-card__waste"><img src={process.env.PUBLIC_URL + '/assets/images/waste.svg' }  onClick={() => {this.props.removeUsers(index)}} width="15px" alt="Icones"/></span>
                            </p> 
                         </div>
                      )
                    })}
          </div>
        </div>
      );
    }
  }

  const mapStateToProps = store => ({
    users: store.users,
    // add: store.users.add,
    remove: store.users.remove
 })

 const mapActionToProps = {
  // addToUsers: addUsersAction,
  removeFromUsers: removeUsersAction,
  dispatchUsers:  dispatchUsersAction,
}

  export default connect ( mapStateToProps, mapActionToProps)(Users);
