import { connect } from 'react-redux';
import underscore from 'underscore';
import React, { Component } from 'react';
import './Winners.scss';

class Winners extends Component {


  //the sampled result is stored in the new table selectedItems
  // sampleList = () => {
  //     const sampled = underscore.sample(this.state.users,3)
  //   //   method 
  //     this.setState({
  //       selectedItems: sampled,
  //     }, this.addSelected)
  // }

  // //create a method that adds 3 persons in array alreadySelected// apply onClick
  // addSelected = () => {
  //   this.setState({
  //       alreadySelected: [...this.state.alreadySelected, ...this.state.selectedItems]
  //   })
  // }

    render() {
      const sampled = underscore.sample(this.props.users, 3)
      //ici je fais appelle à this.props.users car c'est le state général

        return (
            <div className="c-winners">
             <h2 className="c-winners__subtitle">Et les gagnants sont ...</h2> 
             <img className="c-winners__logo" src={process.env.PUBLIC_URL+'/assets/images/coffee-cup.svg'} alt="Logo coffee-cup" />
                  {sampled.map((item, index) => {
                      return (
                        <div>  
                             <input className="c-winners__input"
                              type="c-winners__text"
                              key={index}
                              Value={item.nom_prenom}
                               />
                        </div>  
                      )
                    })}
            </div>
        )
    }
}

const mapStateToProps = store => {
  return {
    users: store.users
  }
}

export default connect(mapStateToProps, null)(Winners);
