import React, { Component } from 'react'
import './Winners.css'
import axios from 'axios';

import underscore from 'underscore';

class Winners extends Component {
  constructor() {
      super();
      this.state = {
          liste: [],
          selectedItems: [],
          alreadySelected: []
      }
  }

  //New syntax async// await
  async componentDidMount() {
    //upon loading, I store in the state the result of my fetch
    const result = await axios.get('http://localhost:6999/liste')
    this.setState({liste: result.data}, () => {
        this.sampleList();
    });
  }

  //.sample is a method to produce a random list of 3 people
  //the sampled result is stored in the new table selectedItems
  sampleList = () => {
      const sampled = underscore.sample(this.state.liste, 3)
      this.setState({
        selectedItems: sampled,
      }, this.alreadySelected)
  }

  //create a method that adds 3 persons in array alreadySelected// apply onClick
  addSelected = () => {
    this.setState({
        alreadySelected: [...this.state.alreadySelected, this.state.selectedItems]
    })
  }

    render(){
        return (
            <div>
                 {this.state.selectedItems.map((item, index) => {
                    return (
                      <div>
                          <p key={index}>
                          <p>{item.nom_prenom}</p>
                          </p> 
                      </div> 
                    )
                })}
            </div>
        )
    }
}

export default Winners;

