import React, { Component } from 'react'
import './Winners.css'
import axios from 'axios';

class Winners extends Component {
    constructor() {
        super();
        this.state = {
            liste: []
        }
        //dès le chargement , je stocke dans le ste, le résultat de mon fetch
        axios.get('http://localhost:6999/liste')
        .then((result) => {
            this.setState({liste: result.data})
        });
    }
    render() {
        return (
            <div>
                {this.state.liste.map((liste, index) => {
                    return (
                        <div>
                            <p key={index}>
                            <p>{liste.nom_prenom}</p>
                            <p>{liste.adresse_mail}</p>
                            </p>
                        </div>
                    )
                })}
            </div>
        )
    }
}

export default Winners;
