            //   users: users
          }
      }
  }


//Action to send mail to 3 winners
export const mailWinnersAction = (data) => {
    return (dispatch) => {
       return axios.post(apiSendUsersUrl, data) 
        .then(response => {
          dispatch(mailWinners(response.data))
        })
        .catch(error => {
          throw(error);
        });
    };
  };

  export const mailWinners =  (data) => {
    return {
      type: MAIL_WINNERS,
      payload: {
        email: data.email
      }
    }
  };
