import { connect } from 'react-redux';
import underscore from 'underscore';
import React, { Component } from 'react';
import {toastr} from 'react-redux-toastr'
import './Winners.scss';

import { mailWinnersAction} from '../Actions/Actions';


const toastrOptions = {
  timeOut: 3000, // by setting to 0 it will prevent the auto close
  onCloseButtonClick: () => console.log('Close button was clicked'),
  showCloseButton: false, // true by default
}

toastr.success('Title', 'Message', toastrOptions)

class Winners extends Component {


  //the sampled result is stored in the new table selectedItems
  // sampleList = () => {
  //     const sampled = underscore.sample(this.state.users,3)
  //   //   method 
  //     this.setState({
  //       selectedItems: sampled,
  //     }, this.addSelected)
  // }

  // //create a method that adds 3 persons in array alreadySelected// apply onClick
  // addSelected = () => {
  //   this.setState({
  //       alreadySelected: [...this.state.alreadySelected, ...this.state.selectedItems]
  //   })
  // }

  onClickMail = (sampled) => {
    this.props.mailWinners(sampled);
    toastr.success('Confirmation', 'Un mail a bien été envoyé aux 3 participants')
  }


    render() {
      const sampled = underscore.sample(this.props.users, 3)
      //ici je fais appelle à this.props.users car c'est le state général

        return (
            <div className="c-winners">
             <h2 className="c-winners__subtitle">Et les gagnants sont ...</h2> 
             {/* <img className="c-winners__logo" src={process.env.PUBLIC_URL+'/assets/images/coffee-cup.svg'} width="50px" alt="Logo coffee-cup" /> */}
                  {sampled.map((item, index) => {
                      return (
                        <div>  
                             <input className="c-winners__input"
                              type="c-winners__text"
                              key={index}
                              Value={item.nom_prenom}
                               />
                        </div>  
                      )
                    })}
              <button className="c-winners__button" onClick={() => this.onClickMail(sampled)}>Préviens les gagnants!</button>
            </div>
        )
    }
}

const mapStateToProps = store => {
  return {
    users: store.users
  }
}

const mapDispatchToProps = {
  mailWinners: mailWinnersAction,
}

export default connect(mapStateToProps, mapDispatchToProps )(Winners);
